INSERT INTO `report` (`id`, `name`, `type`, `sql`, `condition`, `content`, `is_active`, `visit_count`, `fcu`, `lcu`, `fcd`, `lcd`) VALUES (721570880299009, 'cmdb配置信息统计', '配置报表', '<mapper>  \n  <resultMap id=\"tableMap\" type=\"java.util.LinkedHashMap\">  \n    \n   <result property=\"配置模型类型\" column=\"配置模型类型\"/>\n	<result property=\"配置模型名称\" column=\"配置模型名称\"/>\n	<result property=\"配置属性数量\" column=\"配置属性数量\"/>\n	<result property=\"配置项数量\" column=\"配置项数量\"/>\n  </resultMap>\n  \n  <select id=\"tableData\" resultMap=\"tableMap\">\n	SELECT a.ciTypeName AS 配置模型类型,a.ciLabel AS 配置模型名称,a.caCount AS 配置属性数量,b.ccCount AS 配置项数量\n    FROM\n    (\n    SELECT \n		cc.name AS ciTypeName,\n      `label` AS ciLabel,\n      (SELECT COUNT(1) FROM `cmdb_attr` WHERE `ci_id` IN (SELECT `id` FROM `cmdb_ci` WHERE `lft` &lt;= a.`lft` and `rht` &gt;= a.`rht`)) AS caCount\n    FROM `cmdb_ci` a\n    JOIN `cmdb_citype` cc ON a.`type_id` = cc.`id`\n    WHERE `is_abstract` = 0\n    ) AS a\n    JOIN\n    (\n    SELECT ci.`label` AS ciLabel,COUNT(cc.id) AS ccCount\n    FROM \n    cmdb_ci ci \n    LEFT JOIN cmdb_cientity cc ON cc.ci_id = ci.id\n    WHERE ci.`is_abstract` = 0\n    GROUP BY ci.id\n    ) AS b ON a.ciLabel = b.ciLabel\n    ORDER BY b.ccCount DESC\n  </select>\n   \n</mapper>', NULL, '${drawTable({data:\"tableData\",title:\"cmdb统计\"})}', 1, 6, 'd6e87b7eb8655404201553eb546a9dde', 'c8dea216f5bd49349db05cbc87db2f9a', '2022-09-22 13:52:32.332', '2023-04-20 15:26:28.387');
INSERT INTO `report` (`id`, `name`, `type`, `sql`, `condition`, `content`, `is_active`, `visit_count`, `fcu`, `lcu`, `fcd`, `lcd`) VALUES (873821062045696, '工单数量统计列表', '工单报表', '<mapper>\n  <resultMap id=\"tableMap\" type=\"java.util.LinkedHashMap\">    \n    <result property=\"服务名称\"  column=\"服务名称\"/>\n    <result property=\"工单数量\"  column=\"工单数量\"/>    \n  </resultMap>\n  <select id=\"tableData\" resultMap=\"tableMap\">\n	SELECT c.`name` AS 服务名称,COUNT(p.id) AS 工单数量\n    FROM  channel c\n    JOIN  `processtask` p ON p.`channel_uuid` = c.`uuid`\n	WHERE TRUE\n	<if test=\"startTime!=null\">\n		 AND p.`start_time` &gt; #{startTime}\n	</if>\n	<if test=\"endTime!=null\">\n		 AND p.`end_time` &lt; #{endTime}\n	</if>\n    GROUP BY p.channel_uuid\n	ORDER BY 工单数量 DESC\n  </select>\n  \n  <resultMap id=\"totalTableMap\" type=\"java.util.LinkedHashMap\">    \n    <result property=\"工单总数\" column=\"工单总数\"/>   \n  </resultMap>\n  \n  <select id=\"totalTableData\" resultMap=\"totalTableMap\">\n	SELECT COUNT(1) AS 工单总数\n	FROM `processtask`\n	WHERE TRUE\n	<if test=\"startTime!=null\">\n		 AND `start_time` &gt; #{startTime}\n	</if>\n	<if test=\"endTime!=null\">\n		 AND `end_time` &lt; #{endTime}\n	</if>\n  </select>\n\n \n   \n</mapper>', NULL, '<div class=\"ivu-card ivu-card-dis-hover ivu-card-shadow\">\n    <div class=\"ivu-card-body tstable-container tstable-normal border tstable-no-fixedHeader block-large\">\n        <div class=\"tstable-main bg-op\">\n            <table tableName=\"服务工单数量统计列表\" class=\"table-main tstable-body\">\n               \n              <tbody class=\"tbody-main\">\n                 <tr>\n                   <td valign=\"top\">${drawTable({data:\"totalTableData\",title:\"工单总数\"})}</td>\n                   <td valign=\"top\">${drawTable({data:\"tableData\",title:\"服务工单数量统计\"})}</td>\n                </tr>\n              </tbody>\n            </table>\n        </div>\n    </div>\n</div>', 1, 3, 'd6e87b7eb8655404201553eb546a9dde', 'd6e87b7eb8655404201553eb546a9dde', '2023-04-20 15:26:28.374', '2023-04-20 15:26:28.374');
INSERT INTO `report` (`id`, `name`, `type`, `sql`, `condition`, `content`, `is_active`, `visit_count`, `fcu`, `lcu`, `fcd`, `lcd`) VALUES (873821062045697, '工单平均响应时间', '工单报表', '<mapper>\n  <resultMap id=\"userTimeCostMap\" type=\"java.util.LinkedHashMap\">\n    <result property=\"用户\" column=\"用户\"/>\n    <result property=\"平均响应时间\" column=\"平均响应时间\"/>\n  </resultMap>\n  <select id=\"userTimeCostData\" resultMap=\"userTimeCostMap\">\n   select user.user_name as \'用户\',TRUNCATE(sum(TIMESTAMPDIFF(MINUTE,start_time,end_time))/count(psu.processtask_step_id),0) as \'平均响应时间\'\nfrom processtask_step_user psu \njoin user on user.uuid = psu.user_uuid \nWHERE status = \'done\'\nand processtask_id in \n(select id from processtask p\njoin channel ch on p.channel_uuid = ch.uuid\njoin channel_type ct on ch.channel_type_uuid = ct.uuid\nwhere ct.name = \'事件\'\n<if test=\"beforeDay != null\">\n    and DATE_SUB(CURDATE(), INTERVAL #{beforeDay} DAY) &lt;= `start_time`\n</if>\n	<if test=\"taskStatus != null\">\n      and p.status = #{taskStatus}\n   </if>\n   <if test=\"keyword != null and keyword != \'\'\">\n      and p.title like concat(\'%\', #{keyword}, \'%\')\n   </if>\n   <if test=\"owner != null\">\n      and p.owner = #{owner}\n   </if>\n   <if test=\"team != null\">\n	and p.`id` in (select processtask_id from processtask_step_user psu join user_team ut on psu.user_uuid = ut.user_uuid\n	join team t on ut.team_uuid = t.uuid\n	where t.`name` = #{team}\nunion\nselect processtask_id from processtask_step_worker psw join user_team ut on psw.uuid = ut.user_uuid\n	join team t on ut.team_uuid = t.uuid\nwhere type = \'user\' and t.`name` = #{team}\nunion\nselect psw.processtask_id from user_team ut join processtask_step_worker psw on ut.team_uuid = psw.uuid \njoin team t on ut.team_uuid = t.uuid\nwhere psw.type = \'team\' and t.`name` = #{team}\n)\n</if>\n     <if test=\"timeRange == 1\">\n	   and TO_DAYS(p.start_time) = TO_DAYS(NOW())\n     </if>\n     <if test=\"timeRange == 2\">\n	   and YEARWEEK(DATE_FORMAT(p.start_time,\'%Y-%m-%d\')) = YEARWEEK(NOW())\n     </if>\n     <if test=\"timeRange == 3\">\n	   and DATE_FORMAT(p.start_time,\'%Y%m\') = DATE_FORMAT(CURDATE(),\'%Y%m\')\n     </if>\n     <if test=\"timeRange == 4\">\n	   and QUARTER(p.start_time) = QUARTER(NOW())\n     </if>\n     <if test=\"timeRange == 5\">\n	   and YEAR(p.start_time)=YEAR(NOW())\n     </if>\n    <if test=\"score != null\">\n    and p.`id` in (select id from (SELECT\n ROUND(AVG(ps.score)) as score,\n	p.`id` as id\nFROM\n	processtask p\n	join channel ch on p.channel_uuid = ch.uuid\n	join channel_type ct on ch.channel_type_uuid = ct.uuid\n	JOIN processtask_score ps ON p.id = ps.processtask_id\n	where ct.name = \'事件\'\n	GROUP BY\n	p.`id`\n	having score = #{score}) a)\n  </if>\n) \ngroup by psu.user_uuid\norder by \'用户\' desc\n	</select>\n</mapper>', NULL, '${drawTable({data:\"userTimeCostData\", title:\"事件处理平均响应时间（单位：分钟）\"})}', 1, 2, 'fccf704231734072a1bf80d90b2d1de2', 'fccf704231734072a1bf80d90b2d1de2', '2023-04-20 15:26:28.376', '2023-04-20 15:26:28.376');
INSERT INTO `report` (`id`, `name`, `type`, `sql`, `condition`, `content`, `is_active`, `visit_count`, `fcu`, `lcu`, `fcd`, `lcd`) VALUES (873821062045698, '工单明细报表', '工单报表', '<mapper>\n  <resultMap id=\"tableMap\" type = \"java.util.LinkedHashMap\">\n    <result property=\"ID\" column=\"serial_number\"/>\n    <result property=\"标题\" column=\"title\"/>\n    <result property=\"状态\" column=\"status\"/>\n    <result property=\"优先级\" column=\"priorityName\"/>\n    <result property=\"处理人\" column=\"userName\"/>\n    <result property=\"剩余时间(小时)\" column=\"expireTime\"/>\n    <result property=\"上报人\" column=\"ownerName\"/>\n    <result property=\"上报时间\" column=\"start_time\"/>\n  </resultMap>\n  <select id=\"tableData\" resultMap=\"tableMap\">\n\n\n\nselect \nIFNULL(pt.serial_number,\'-\') as \'serial_number\',\n<![CDATA[\nCONCAT(\'<a target=\"_blank\" href=\"/demo/process.html#/task-detail?processTaskId=\',pt.id,\'\">\',title,\'</a>\')AS \'title\',\n]]>\nu.user_name as \'ownerName\',\n(case pt.status \nwhen \'pending\' then \'待处理\' \nwhen \'running\' then \'处理中\'\nwhen \'aborted\' then \'已取消\'\nwhen \'succeed\' then \'已完成\'\nwhen \'failed\' then \'异常\'\nwhen \'aborting\' then \'终止中\'\nwhen \'draft\' then \'未提交\'\nwhen \'scored\' then \'已评分\'\nwhen \'back\' then \'已回退\'\nwhen \'hang\' then \'已挂起\' end) as \'status\',\np.`name` as \'priorityName\',\nIFNULL(GROUP_CONCAT(DISTINCT psu.user_name),IFNULL(GROUP_CONCAT( DISTINCT IFNULL(u1.user_name,IFNULL(t1.`name`,r1.`name`))),\'-\')) as userName,\npt.start_time,\nIFNULL(max(TIMESTAMPDIFF(HOUR,now(),pst.expire_time)),\'-\') as expireTime\nfrom processtask pt\nleft join user u on pt.owner = u.uuid\nleft join priority p on pt.priority_uuid = p.uuid\nleft join processtask_step_worker psw on pt.id = psw.processtask_id\nLEFT JOIN user u1 on psw.uuid = u1.uuid\nLEFT JOIN team t1 on psw.uuid = t1.uuid\nleft join role r1 on psw.uuid = r1.uuid\nleft join processtask_step_user psu on pt.id = psu.processtask_id\nleft join processtask_step ps on pt.id = ps.processtask_id and ps.status not in (\'succeed\') and pt.`status` not in (\'aborted\',\'draft\')\nleft join processtask_step_sla pss on ps.id = pss.processtask_step_id\nleft join processtask_sla_time pst on pss.sla_id = pst.sla_id\nwhere pt.`is_deleted` = 0\n<if test=\"beforeDay != null and beforeDay != \'\'\">\n    and DATE_SUB(CURDATE(), INTERVAL #{beforeDay} DAY) &lt;= pt.`start_time`\n</if>\n<if test=\"taskStatus != null and taskStatus != \'\'\">\n   and pt.`status` = #{taskStatus}\n</if>\n<if test=\"keyword != null and keyword != \'\'\">\n   and pt.title like concat(\'%\', #{keyword}, \'%\')\n</if>\n<if test=\"owner != null and owner != \'\'\">\n   and pt.`owner` = #{owner}\n</if>\n<if test=\"team != null and team != \'\'\">\n	and pt.`id` in (select processtask_id from processtask_step_user psu join user_team ut on psu.user_uuid = ut.user_uuid\n	join team t on ut.team_uuid = t.uuid\n	where t.`name` = #{team}\nunion\nselect processtask_id from processtask_step_worker psw join user_team ut on psw.uuid = ut.user_uuid\n	join team t on ut.team_uuid = t.uuid\nwhere type = \'user\' and t.`name` = #{team}\nunion\nselect psw.processtask_id from user_team ut join processtask_step_worker psw on ut.team_uuid = psw.uuid \njoin team t on ut.team_uuid = t.uuid\nwhere psw.type = \'team\' and t.`name` = #{team}\n)\n</if>\n<if test=\"score != null\">\n    and pt.`id` in (select id from (SELECT\n ROUND(AVG(ps.score)) as score,\n	p.`id` as id\nFROM\n	processtask p\n	join channel ch on p.channel_uuid = ch.uuid\n	join channel_type ct on ch.channel_type_uuid = ct.uuid\n	JOIN processtask_score ps ON p.id = ps.processtask_id\n	GROUP BY\n	p.`id`\n	having score = #{score}) a)\n</if>\nGROUP BY pt.id\n\n  </select>\n</mapper>', NULL, '${drawTable({data:\"tableData\", title:\"工单列表\"})}', 1, 3, 'fccf704231734072a1bf80d90b2d1de2', 'c8dea216f5bd49349db05cbc87db2f9a', '2023-04-20 15:26:28.384', '2023-05-04 12:08:13.884');
